// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

/// @title SnarkConstants
/// @notice This contract contains constants related to the SNARK
/// components of MACI.
contract SnarkConstants {
  /// @notice The scalar field
  uint256 internal constant SNARK_SCALAR_FIELD =
    21888242871839275222246405745257275088548364400416034343698204186575808495617;

  /// @notice The public key here is the first Pedersen base
  /// point from iden3's circomlib implementation of the Pedersen hash.
  /// Since it is generated using a hash-to-curve function, we are
  /// confident that no-one knows the private key associated with this
  /// public key. See:
  /// https://github.com/iden3/circomlib/blob/d5ed1c3ce4ca137a6b3ca48bec4ac12c1b38957a/src/pedersen_printbases.js
  /// Its hash should equal
  /// 6769006970205099520508948723718471724660867171122235270773600567925038008762.
  uint256 internal constant PAD_PUBKEY_X =
    10457101036533406547632367118273992217979173478358440826365724437999023779287;
  uint256 internal constant PAD_PUBKEY_Y =
    19824078218392094440610104313265183977899662750282163392862422243483260492317;

  /// @notice The Keccack256 hash of 'Maci'
  uint256 internal constant NOTHING_UP_MY_SLEEVE =
    8370432830353022751713833565135785980866757267633941821328460903436894336785;
}

contracts/crypto/Verifier.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

import { Pairing } from "./Pairing.sol";
import { SnarkConstants } from "./SnarkConstants.sol";
import { SnarkCommon } from "./SnarkCommon.sol";
import { IVerifier } from "../interfaces/IVerifier.sol";

/// @title Verifier
/// @notice a Groth16 verifier contract
contract Verifier is IVerifier, SnarkConstants, SnarkCommon {
  struct Proof {
    Pairing.G1Point a;
    Pairing.G2Point b;
    Pairing.G1Point c;
  }

  using Pairing for *;

  uint256 public constant PRIME_Q = 21888242871839275222246405745257275088696311157297823662689037894645226208583;

  /// @notice custom errors
  error InvalidProofQ();
  error InvalidInputVal();

  /// @notice Verify a zk-SNARK proof
  /// @param _proof The proof
  /// @param vk The verifying key
  /// @param input The public inputs to the circuit
  /// @return isValid Whether the proof is valid given the verifying key and public
  ///          input. Note that this function only supports one public input.
  ///          Refer to the Semaphore source code for a verifier that supports
  ///          multiple public inputs.
  function verify(
    uint256[8] memory _proof,
    VerifyingKey memory vk,
    uint256 input
  ) public view override returns (bool isValid) {
    Proof memory proof;
    proof.a = Pairing.G1Point(_proof[0], _proof[1]);
    proof.b = Pairing.G2Point([_proof[2], _proof[3]], [_proof[4], _proof[5]]);
    proof.c = Pairing.G1Point(_proof[6], _proof[7]);

    // Make sure that proof.A, B, and C are each less than the prime q
    checkPoint(proof.a.x);
    checkPoint(proof.a.y);
    checkPoint(proof.b.x[0]);
    checkPoint(proof.b.y[0]);
    checkPoint(proof.b.x[1]);
    checkPoint(proof.b.y[1]);
    checkPoint(proof.c.x);
    checkPoint(proof.c.y);

    // Make sure that the input is less than the snark scalar field
    if (input >= SNARK_SCALAR_FIELD) {
      revert InvalidInputVal();
    }

    // Compute the linear combination vk_x
    Pairing.G1Point memory vkX = Pairing.G1Point(0, 0);

    vkX = Pairing.plus(vkX, Pairing.scalarMul(vk.ic[1], input));

    vkX = Pairing.plus(vkX, vk.ic[0]);

    isValid = Pairing.pairing(
      Pairing.negate(proof.a),
      proof.b,
      vk.alpha1,
      vk.beta2,
      vkX,
      vk.gamma2,
      proof.c,
      vk.delta2
    );
  }

  function checkPoint(uint256 point) internal pure {
    if (point >= PRIME_Q) {
      revert InvalidProofQ();
    }
  }
}

contracts/gatekeepers/EASGatekeeper.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

import { SignUpGatekeeper } from "./SignUpGatekeeper.sol";
import { IEAS } from "../interfaces/IEAS.sol";

/// @title EASGatekeeper
/// @notice A gatekeeper contract which allows users to sign up to MACI
/// only if they've received an attestation of a specific schema from a trusted attester
contract EASGatekeeper is SignUpGatekeeper, Ownable {
  // the reference to the EAS contract
  IEAS private immutable eas;

  // the schema to check against
  bytes32 public immutable schema;

  // the trusted attester
  address public immutable attester;

  /// @notice the reference to the MACI contract
  address public maci;

  // a mapping of attestations that have already registered
  mapping(bytes32 => bool) public registeredAttestations;

  /// @notice custom errors
  error AttestationRevoked();
  error AlreadyRegistered();
  error AttesterNotTrusted();
  error NotYourAttestation();
  error InvalidSchema();
  error OnlyMACI();
  error ZeroAddress();

  /// @notice Deploy an instance of EASGatekeeper
  /// @param _eas The EAS contract
  /// @param _attester The trusted attester
  /// @param _schema The schema UID
  constructor(address _eas, address _attester, bytes32 _schema) payable Ownable() {
    if (_eas == address(0) || _attester == address(0)) revert ZeroAddress();
    eas = IEAS(_eas);
    schema = _schema;
    attester = _attester;
  }

  /// @notice Adds an uninitialised MACI instance to allow for token signups
  /// @param _maci The MACI contract interface to be stored
  function setMaciInstance(address _maci) public override onlyOwner {
    if (_maci == address(0)) revert ZeroAddress();
    maci = _maci;
  }

  /// @notice Register an user based on their attestation
  /// @dev Throw if the attestation is not valid or just complete silently
  /// @param _user The user's Ethereum address.
  /// @param _data The ABI-encoded schemaId as a uint256.
  function register(address _user, bytes memory _data) public override {
    // decode the argument
    bytes32 attestationId = abi.decode(_data, (bytes32));

    // ensure that the caller is the MACI contract
    if (maci != msg.sender) revert OnlyMACI();

    // ensure that the attestation has not been registered yet
    if (registeredAttestations[attestationId]) revert AlreadyRegistered();

    // register the attestation so it cannot be called again with the same one
    registeredAttestations[attestationId] = true;

    // get the attestation from the EAS contract
    IEAS.Attestation memory attestation = eas.getAttestation(attestationId);

    // the schema must match
    if (attestation.schema != schema) revert InvalidSchema();

    // we check that the attestation attester is the trusted one
    if (attestation.attester != attester) revert AttesterNotTrusted();

    // we check that it was not revoked
    if (attestation.revocationTime != 0) revert AttestationRevoked();

    // one cannot register an attestation for another user
    if (attestation.recipient != _user) revert NotYourAttestation();
  }
}

